sys_script_include.script:
var NameFromEmail = Class.create();
NameFromEmail.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getName: function() {
        var userName = new GlideRecord('sys_user');
        var userEmail = this.getParameter('sysparm_userID');
		gs.addInfoMessage(userEmail);
        userName.addQuery('email', userEmail);
        userName.query();
        if (userName.next()) {
            var ghh = userName.first_name + '';
			gs.addInfoMessage(ghh);
			//return ghh;
        }
		gs.addInfoMessage("outside if -> " + ghh);
		return ghh + '';
    },


    type: 'NameFromEmail'
});
